<?php$con = mysql_connect("localhost","root" ,"");// Check connectionif (!$con)  {  echo "Failed to connect to MySQL: " . mysql_connect_error();  }  mysql_select_db('my_db1');  $sql = "select * from datatable"; $result = mysql_query($sql);$json = array();if(mysql_num_rows($result)){while($row=mysql_fetch_row($result)){$item = array('id' => $row[0], 'title' => $row[1], 'company' => $row[2], 'image' => $row[3], 'price' => $row[4],);$json[]= $item;}echo json_encode($json);}mysql_close($con);?>